import "./payment.css";
import { DataGrid } from "@material-ui/data-grid";

import { FaUserShield, FaBars } from 'react-icons/fa';

import { DeleteOutline } from "@material-ui/icons";
import { userRows } from "../../dummyData";
import { Link } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from 'axios';

export default function UserList({ handleToggleSidebar }) {
    const [data, setData] = useState([]);

    const handleDelete = (id) => {
        setData(data.filter((item) => item.id !== id));
    };
    useEffect(() => {

        axios.get('https://api-kearekisa.herokuapp.com/admin/payments')
          .then(res => {
            const cat = res.data;
            // console.log(cat[0])
    
            const userData = cat.map((item) => {
              item.id = item._id;
              return {
                ...item
              }
            })
            setData(userData)
    
          })
          .catch(err => {
            alert(err);
          })
      }, []);

      const verify = async(id) => {
          console.log("id:", id);
        await axios.patch('https://api-kearekisa.herokuapp.com/admin/verifypremium/'+id)
        .then(res => {
            if(res.data.msg === "Premium set"){
                alert("Payment Verified")
            }
        })
        .catch(err => {
            alert(err);
        })
      }
      

    const columns = [
        {
            field: "User",
            headerName: "User",
            width: 200,
            renderCell: (params) => {
                return (
                    <div className="UserListItem">
                        
                        {params.row.userid}
                    </div>
                );
            },
        },
        {
            field: "Email",
            headerName: "Email",
            width: 200,
            renderCell: (params) => {
                return (
                    <div className="UserListItem">
                       
                        {params.row.useremail}
                    </div>
                );
            },
        },
        {
            field: "Amount Paid",
            headerName: "Amount Paid",
            width: 200,
            renderCell: (params) => {
                return (
                    <div className="UserListItem">
                        {params.row.amountpaid}
                    </div>
                );
            },
        },
        {
            field: "Paytime",
            headerName: "Paytime",
            width: 200,
            renderCell: (params) => {
                return (
                    <div className="UserListItem">
                        {params.row.paytime}
                    </div>
                );
            },
        },
        {
            field: "action",
            headerName: "Action",
            width: 150,
            renderCell: (params) => {
                return (

                    <div className="UserListEdit" onClick={()=>verify(params.row.userid)}>
                        <p style={{textAlign:'center', marginTop:-20}}>Verify</p>
                    </div>
                );
            },
        },
    ];

    return (
        <div className="UserList">
            <div
                style={{
                    width: 35,
                    height: 35,
                    height: 35,
                    color: '#353535',
                    textAlign: 'center',
                    borderRadius: '50%',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    fontSize: 20,
                    cursor: 'pointer'
                }}
                onClick={() => handleToggleSidebar(true)}>
                <FaBars />
            </div>
            <h1 className="userTitle" style={{ textAlign: 'center', margin: 5 }}>Payments</h1>
            <DataGrid
                rows={data}
                disableSelectionOnClick
                columns={columns}
                pageSize={8}
            />
        </div>
    );
}
